const TxtRotate = function(el, toRotate, period) {
  // console.log('creating object', el)
  this.toRotate = toRotate
  this.el = el
  this.loopNum = 0
  this.period = parseInt(period, 10) || 2000
  this.txt = ''
  this.tick = tick
  this.isDeleting = false
  console.log(this.toRotate)
  return this.tick(this)
}

const tick = function(self) {
  // console.log(self)
  const i = self.loopNum % self.toRotate.length
  const fullTxt = self.toRotate[i]

  if (self.isDeleting) {
    self.txt = fullTxt.substring(0, self.txt.length - 1)
  } else {
    self.txt = fullTxt.substring(0, self.txt.length + 1)
  }

  self.el.innerHTML = '<span class="wrap">'+self.txt+'</span>'

  const that = self
  let delta = 130 - Math.random() * 100

  if (self.isDeleting) { delta /= 2 }

  if (!self.isDeleting && self.txt === fullTxt) {
    delta = self.period
    self.isDeleting = true
  } else if (self.isDeleting && self.txt === '') {
    self.isDeleting = false
    self.loopNum++
    delta = 500
  }

  setTimeout(function() {
    that.tick(that)
  }, delta)
}

module.exports = TxtRotate
