import React from 'react'
import './style.css'
import Typing from 'react-typing-animation';

const TxtRotate = require('../../utils/textRotate')


class Photothingy extends React.Component {
  state = {
    word: '',
    end: false
  }

  componentDidMount() {
    const captions = this.props.captions
    setInterval(() => {
      this.setState({
        word: captions[Math.floor(Math.random()*captions.length)]
      })
    }, 5000)

  }

  getCaption = (captions) => {
    return this.state.word
  }


  render () {
    return (
      <div className={this.props.item} data-aos={this.props.aosAction || 'fade-up'}>
        {/* <img src={photo}/> */}
        <p className="beach">
          <Typing loop={true} cursor={false}>
            <span>{this.state.word}</span>
            <Typing.Backspace count={this.state.word.length} delay={1000} />
          </Typing>
          {/*<Typing>*/}
          {/*  <span>This span will get typed, then erased.</span>*/}
          {/*  <Typing.Backspace count={30} />*/}
          {/*</Typing>*/}
          {/*<span*/}
          {/*  className="txt-rotate"*/}
          {/*  data-period="2500"*/}
          {/*  data-rotate={this.props.captions}>{this.startAnimation(this, this.props.captions)}*/}
          {/*</span>*/}
        </p>
      </div>
    )
  }
}

function perform (captions) {
  const item = captions[Math.floor(Math.random()*captions.length)]
}


export default Photothingy
