import React from 'react'
import './style.css'
import Typing from 'react-typing-animation';

const TxtRotate = require('../../utils/textRotate')


class Photothingy extends React.Component {
  state = {
    word: '',
    end: false
  }

  componentDidMount() {
    // this.startAnimation(this.props.captions)
  }

  startAnimation = (el, captions) => {
    const inner =  new TxtRotate(el, captions, 2500)
    const inner2 =  new TxtRotate(el, captions, 2500)
    return inner.txt + inner2.txt
  }


  render () {
    return (
      <div className={this.props.item} data-aos={this.props.aosAction || 'fade-up'}>
        {/* <img src={photo}/> */}
        <p className="beach">
          <Typing>
            <span>{this.props.captions}</span>
          </Typing>
          {/*<span*/}
          {/*  className="txt-rotate"*/}
          {/*  data-period="2500"*/}
          {/*  data-rotate={this.props.captions}>{this.startAnimation(this, this.props.captions)}*/}
          {/*</span>*/}
        </p>
      </div>
    )
  }
}

function perform (captions) {
  const item = captions[Math.floor(Math.random()*captions.length)]
}


export default Photothingy
